package com.smile.dto;import com.smile.model.Options;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class SurveyRequestDto {    private String title;    private QuestionRequestDto question;    private List<Options>options;    @Override    public String toString() {        return "SurveyRequestDto{" +                "title='" + title + '\'' +                ", question=" + question +                ", options=" + options +                '}';    }}