package com.smile.dto;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.time.LocalDateTime;@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class SurveyResponseDto {    private String batchNumber;    private String message;    private LocalDateTime dateCreated;    @Override    public String toString() {        return "SurveyResponseDto{" +                "message=" + message +                ", dateCreated=" + dateCreated +                '}';    }}