package com.smile.model;import jakarta.persistence.*;import lombok.*;import java.util.List;@Entity@Getter@Setter@Builder@NoArgsConstructor@AllArgsConstructorpublic class Questions {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    private Long surveyId;    private String questionTitle;    @OneToMany(cascade = {CascadeType.MERGE, CascadeType.PERSIST}, fetch = FetchType.LAZY)    private List<Options>options;}