package com.smile.model;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Entity@AllArgsConstructor@NoArgsConstructor@Getter@Setterpublic class Response {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    private String batchNumber;    private String questionId;    private String optionId;    private String response;    private Integer totalCount;}