package com.smile.utility;import io.swagger.v3.oas.models.OpenAPI;import io.swagger.v3.oas.models.info.Info;import io.swagger.v3.oas.models.info.License;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class Config {    @Bean    public OpenAPI customOpenAPI(@Value("${springdoc.version}") String appVersion) {        return new OpenAPI().info(new Info().title("Smile API's")                .version(appVersion)                .description("This is a basic API development tutorial documentation")                .termsOfService("http://swagger.io/terms/")                .license(new License().name("Apache 2.0")                        .url("http://springdoc.org")));    }}