package com.smile.utility;import java.time.LocalDateTime;public class SurveyHelper {    public static String createBatchNumber(){        return String.valueOf(LocalDateTime.now().getYear()).concat(String.valueOf(LocalDateTime.now()                        .getDayOfMonth())).concat(String.valueOf(LocalDateTime.now().getHour()))                .concat(String.valueOf(LocalDateTime.now().getMinute()))                .concat(String.valueOf(LocalDateTime.now().getSecond()));    }}